/* Main Style Sheet */
/* Base Styles tor the webside */
body {
  font-family: "Roboto", sans-serif;
}

* {
  box-sizing: border-box;
}

/* Button Stylessheet */
.btn {
  background-color: #01a5e2;
  text-decoration: none;
  color: white;
  width: 197px;
  height: 88px;
  font-weight: 700;
  font-size: 1.3rem;
  display: flex;
  justify-content: center;
  align-items: center;
}
.btn-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
}

/* Styles for the Modal component */
.modal {
  position: fixed;
  z-index: 5;
  background-color: rgba(0, 0, 0, 0.36);
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  opacity: 0;
  visibility: hidden;
  -webkit-transform: scale(1.2);
          transform: scale(1.2);
  transition: all 0.3s ease-out;
  overflow-y: auto;
  /* Desktop Styles */
}
.modal__inner {
  display: flex;
  justify-content: center;
  align-items: center;
}
.modal--is-visible {
  opacity: 1;
  visibility: visible;
  -webkit-transform: scale(1);
          transform: scale(1);
}
.modal__content {
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
}
.modal__title {
  color: white;
  position: absolute;
  -webkit-transform: translate(0%, -200%);
          transform: translate(0%, -200%);
}
.modal__categories-list {
  display: flex;
  justify-content: flex-start;
  flex: initial;
  flex-wrap: wrap;
  margin-right: 0px;
}
.modal__categories-list-item {
  width: 50%;
  height: 82px;
  padding: 0.3rem;
  background-color: white;
  transition: background-color 0.3s linear, color 0.3s linear;
  margin-bottom: 12px;
  color: #01a5e2;
  font-weight: 700;
}
.modal__categories-detail {
  background-color: #01a5e2;
  width: 100%;
  height: 500px;
  padding: 1rem;
  overflow: hidden;
  color: white;
}
.modal__close {
  position: relative;
  right: -45vw;
  font-size: 2rem;
  -webkit-transform: scaleX(1.3);
          transform: scaleX(1.3);
  -webkit-transform-origin: 100% 0;
          transform-origin: 100% 0;
  cursor: pointer;
  color: white;
}
.modal .br {
  border-right: 12px solid #a3a3a3;
}
.modal .active {
  background-color: #01a5e2;
  color: white;
}
@media only screen and (min-width: 821px) {
  .modal__categories-list {
    width: 320px;
    margin-right: 12px;
  }
  .modal__close {
    left: 850px;
    top: -66px;
  }
  .modal__categories-detail {
    width: 500px;
    margin-bottom: 21px;
  }
  .modal__inner {
    height: 100vh;
  }
}
